// src/store.js
import { createStore } from 'vuex';

export default createStore({
  state: {
    user: null,  // Stocăm utilizatorul
  },
  mutations: {
    setUser(state, user) {
      state.user = user;  // Setăm utilizatorul
    },
    logout(state) {
      state.user = null;  // Eliminăm utilizatorul la logout
    },
  },
  actions: {
    login({ commit }, user) {
      commit('setUser', user);  // Adăugăm utilizatorul la logare
    },
    logout({ commit }) {
      commit('logout');  // Eliminăm utilizatorul la logout
    },
  },
  getters: {
    isAuthenticated(state) {
      return !!state.user;  // Returnează dacă utilizatorul este autentificat
    },
    getUser(state) {
      return state.user;  // Returnează utilizatorul
    },
  },
});
